import React, { useState } from 'react';
import { Text, View, TouchableOpacity, TextInput, Alert, Picker } from 'react-native';
import estilos from './estilos';
import { criarRepositoriosDoUsuario } from '../../servicos/requisicoes/repositorios';

export default function CriarRepositorio({ route, navigation }) {
    const [nome, setNome] = useState('Presente');
    const [data, setData] = useState('');

    async function criar(){
        const resultado = await criarRepositoriosDoUsuario(
            route.params.id,
            nome,
            data,
        )

        if( resultado === 'sucesso'){
            Alert.alert('Status criado!')
            navigation.goBack();
        }
        else {
            Alert.alert('Erro ao criar status')
        }

    }

    return (
        <View style={estilos.container}>
            <Picker
                nome={nome}
                style={estilos.entrada}
                onValueChange={(itemValue, itemIndex) => setNome(itemValue)}
            >
                <Picker.Item label="Presente" value="Presente" />
                <Picker.Item label="Ausente" value="Ausente" />
            </Picker>
            <TextInput
                placeholder="Data de criação"
                autoCapitalize="none"
                style={estilos.entrada}
                value={data}
                onChangeText={setData}
            />
            <TouchableOpacity style={estilos.botao}
                onPress={criar}
            >
                <Text style={estilos.textoBotao}>
                    Criar
                </Text>
            </TouchableOpacity>
        </View>
    );
}
